                <td>
                {/*<select  id="tre_code_input">
                {order.map(item1=>{
                  return<option value={item1.id}>{item1.trek_id}</option>
                })}
              </select>*/}
                <input type="text" value={item.oreder.trek_id} />
                </td>
                <td>
                <select  id="status_input">
                <option value="1">Дабавлен</option>
